<?php

pload('packfire.event.pObservableEvent');
pload('packfire.event.pEventObserver');
pload('packfire.core.IObserver');

/**
 * Test class for pObservableEvent.
 * Generated by PHPUnit on 2012-09-17 at 07:44:11.
 */
class pObservableEventTest extends PHPUnit_Framework_TestCase implements IObserver {

    /**
     * @var pObservableEvent
     */
    protected $object;
    
    /**
     * @var integer
     */
    protected $counter = 0;

    /**
     * Sets up the fixture, for example, opens a network connection.
     * This method is called before a test is executed.
     */
    protected function setUp() {
        $this->object = new pObservableEvent($this);
        $this->object->attach(new pEventObserver(array($this, 'observer')));
        $this->object->attach($this);
    }
    
    public function observer($observable, $arg = null){
        if($this === $observable){
            $this->counter += $arg;
        }
    }
    
    public function updated($observable, $arg = null){
        $this->observer($observable, $arg);
    }

    /**
     * Tears down the fixture, for example, closes a network connection.
     * This method is called after a test is executed.
     */
    protected function tearDown() {
        
    }

    /**
     * @covers pObservableEvent::notify
     */
    public function testNotify() {
        $this->object->notify(5);
        $this->assertEquals(10, $this->counter);
    }

}